import React, { useState } from 'react';
import './ChatInput.css';
import axios from 'axios';

const ChatInput = ({ addMessage, onSend }) => {
  const [input, setInput] = useState('');
  const [isSending, setIsSending] = useState(false);

  const handleSend = async () => {
    if (input.trim()) {
      addMessage(input, 'user');
      setIsSending(true);
      const messageToSend = input;
      setInput('');

      try {
        if (messageToSend.trim().toLowerCase() === 'yes') {
          // Simulate backend response or trigger file download directly
          const downloadUrl = 'http://localhost:5000/get_certificate'; // Replace with actual endpoint
          
          // Create a hidden anchor element
          const link = document.createElement('a');
          link.href = downloadUrl;
          link.setAttribute('download', ''); // Set the download attribute to force download
          link.style.display = 'none'; // Hide the anchor element
          document.body.appendChild(link);
          
          // Programmatically click the anchor to trigger download
          link.click();
          
          // Clean up: remove the anchor element
          document.body.removeChild(link);

          // Optionally, inform the user about the download action
          addMessage('Certificate is downloading...', 'bot');
        } else {
          // Handle other messages or call your backend as needed
          const response = await fetch('http://127.0.0.1:5000/answer', {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify({ question: messageToSend })
          });

          if (!response.ok) {
            throw new Error('Network response was not ok');
          }

          const data = await response.json();
          addMessage(data.answer, 'bot');
        }
      } catch (error) {
        console.error('There was a problem with the fetch operation:', error);
      }

      setIsSending(false);
      onSend(); // Call the onSend function when the message is sent
    }
  };

  return (
    <div className="chat-input">
      <input
        type="text"
        value={input}
        onChange={(e) => setInput(e.target.value)}
        onKeyDown={(e) => e.key === 'Enter' && !isSending && handleSend()}
        placeholder="ഒരു സന്ദേശം ടൈപ്പ് ചെയ്യുക..."
        aria-label="Type your message"
      />
      <button onClick={handleSend} disabled={isSending} aria-label="Send message">
        {isSending ? 'Sending...' : 'അയയ്ക്കുക'}
      </button>
    </div>
  );
};

export default ChatInput;
