import React, { useState } from 'react';
import axios from 'axios';

const PdfDownloader = ({ triggerPDFFetch }) => {
  const [pdfFiles, setPdfFiles] = useState([]);

  const fetchPDFs = async () => {
    try {
      const response = await axios.get('http://localhost:5000/get_pdfs');
      setPdfFiles(response.data);
    } catch (error) {
      console.error('Error fetching PDFs:', error);
    }
  };

  const handleDownload = async (filename) => {
    try {
      const response = await axios.get(`http://localhost:5000/get_blob?filename=${filename}`, {
        responseType: 'blob'  // Ensure responseType is set to 'blob' to handle binary data
      });

      // Create a URL for the blob response
      const url = window.URL.createObjectURL(new Blob([response.data]));

      // Create a temporary anchor element and set the download attribute
      const link = document.createElement('a');
      link.href = url;
      link.setAttribute('download', filename);

      // Append the anchor element to the body and click it programmatically
      document.body.appendChild(link);
      link.click();

      // Clean up: remove the anchor element and revoke the URL object to free up resources
      document.body.removeChild(link);
      window.URL.revokeObjectURL(url);
    } catch (error) {
      console.error('Error downloading PDF:', error);
    }
  };

  return (
    <div className="pdf-list-container">
      <h2>PDF Files</h2>
      <ul>
        {pdfFiles.map((pdf, index) => (
          <li key={index}>
            <button onClick={() => handleDownload(pdf.filename)}>
              {pdf.filename}
            </button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default PdfDownloader;
