import React, { useState } from 'react';
import './App.css';
import ChatBox from './components/ChatBox';
import ChatInput from './components/ChatInput';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import Navbar from './components/Navbar';
import AboutUs from './components/AboutUs';
import PDFList from './components/EPdfDownloader.js'; // Assuming PDFList component is in PDFList.js

function Chat() {
  const [messages, setMessages] = useState([]);
  const [isVisible, setIsVisible] = useState(true);

  const addMessage = (message, sender) => {
    setMessages(prevMessages => [...prevMessages, { text: message, sender, feedback: '' }]);
  };

  const handleSendClick = () => {
    setIsVisible(false);
  };

  const triggerPDFFetch = async () => {
    try {
      await PDFList.fetchPDFs(); // Assuming fetchPDFs is a static function in PDFList
    } catch (error) {
      console.error('Error fetching PDFs:', error);
    }
  };

  return (
    <div className="main-content">
      <div className="app-container">
        <div className="sidebar">
          <PDFList />
        </div>
      </div>
      <div className='header'>
       <h1>ജന സഹായി</h1>
      </div>
      <div className="chat-container">

          {isVisible && <h2>എനിക്ക് നിങ്ങളെ എങ്ങനെ സഹായിക്കാനാകും?</h2>}
          <ChatBox messages={messages} />
          <ChatInput addMessage={addMessage} onSend={handleSendClick} triggerPDFFetch={triggerPDFFetch} />

      <Navbar/>
      </div>
    </div>
  );
}

function App() {
  return (
    <Router>
      <div className="App">
        <Routes>
          <Route path="/" element={<Chat />} />
          <Route path="/aboutus" element={<AboutUs />} />
        </Routes>
      </div>
    </Router>
  );
}

export default App;
